const routerOutput = $input.first().json.output || $input.first().json.text;
const session = $('Load Session (getWorkflowStaticData)').first().json.session;
const userMessage = $('Load Session (getWorkflowStaticData)').first().json.userMessage;
const userId = $('Load Session (getWorkflowStaticData)').first().json.userId;
const sessionId = $('Load Session (getWorkflowStaticData)').first().json.sessionId;

let routerResponse;
try {
  if (typeof routerOutput === 'object') {
    routerResponse = routerOutput;
  } else {
    const jsonMatch = routerOutput.match(/\{[\s\S]*?\}/);
    if (jsonMatch) {
      routerResponse = JSON.parse(jsonMatch[0]);
    } else {
      throw new Error('No JSON');
    }
  }
} catch (e) {
  routerResponse = { agent: 'general', isNewRequest: true };
}

let targetAgent;
if (routerResponse.isNewRequest) {
  targetAgent = routerResponse.agent;
} else if (session.activeAgent) {
  targetAgent = session.activeAgent;
} else {
  targetAgent = routerResponse.agent;
}

return [{
  json: {
    targetAgent,
    activeAgent:targetAgent,
    chatInput: userMessage,
    userId,
    session,
    sessionId
  }
}];

